import React, { useEffect, useState } from "react";
import { getFormulaire } from "../services/ApiService";

export default function FormulaireList() {
    const [formulaire, setFormulaire] = useState([]);

    useEffect(() => {
        getFormulaire()
            .then(res => {
                console.log("res from Api ", res);
                setFormulaire(res);
            });
    }, []);

    const handleInputChange = (index, field, value) => {
        const updatedFormulaire = formulaire.map((item, i) => {
            if (index === i) {
                return { ...item, [field]: value };
            }
            return item;
        });
        setFormulaire(updatedFormulaire);
    };

    return (
        <>
            <h3>Formulaire</h3>
            {formulaire.map((form, index) => (
                <div key={index} style={{ marginBottom: "10px" }}>
                    <label>Nom:</label>
                    <input
                        type="text"
                        value={form.nom}
                        onChange={e =>
                            handleInputChange(
                                index,
                                "nom",
                                e.target.value
                            )
                        }
                    />
                    <label>Pr√©nom:</label>
                    <input
                        type="text"
                        value={form.prenom}
                        onChange={e =>
                            handleInputChange(
                                index,
                                "prenom",
                                e.target.value
                            )
                        }
                    />
                    <label>Email:</label>
                    <input
                        type="text"
                        value={form.mail}
                        onChange={e =>
                            handleInputChange(
                                index,
                                "mail",
                                e.target.value
                            )
                        }
                    />
                    <button type="submit">Envoyer</button>
                </div>
            ))}
        </>
    );
}
