import React, { useEffect, useState } from "react";
import { getFormulaire, addFormulaire } from "../services/ApiService";
import AddFormulaire from "./AddFormulaire";

export default function FormulaireList() {
    const [formulaires, setFormulaires] = useState([])
    const [showAddFormulaireFrom, setShowAddFormulaireFrom] = useState(false);

    useEffect(() => {
        let isMounted = true;

        getFormulaire()
            .then(res => {
                if (isMounted) {
                    console.log("res from Api ", res);
                    setFormulaires(res);
                }
            })
            .catch(error => {
                console.error("Error fetching formulaires:", error);
            });

        return () => {
            isMounted = false;
        };
    }, []);

    const handleAddSubmit = (Formulaire) => {
        addFormulaire(Formulaire
            )
            .then(res => {
                setFormulaires(res); // Add the new formulaire to the existing array
                setShowAddFormulaireFrom(false);
            })
            .catch(error => {
                console.error("Error adding formulaire:", error);
            });
    };

    return (
        <>
            <h3>Formulaire</h3>
            <table>
                <thead>
                    <tr>
                        <th>Nom</th>
                        <th>Pr√©nom</th>
                        <th>Email</th>
                    </tr>
                </thead>
                <tbody>
                    {formulaires.map(formulaire => {
                       return <tr Formulaire key={formulaire.id} formulaire={formulaire}>
                            <td>{formulaire.nom}</td>
                            <td>{formulaire.prenom}</td>
                            <td>{formulaire.mail}</td>
                            
                        </tr>
                })}
                </tbody>
            </table>
            <button onClick={() => setShowAddFormulaireFrom(true)}>Add New Person</button>
            {showAddFormulaireFrom && <AddFormulaire handleAddSubmit={handleAddSubmit} />}
        </>
    );
}
