import React, { useEffect, useState } from "react";
import { getFormulaire, addFormulaire } from "../services/ApiService";
import AddFormulaire from "./AddFormulaire";

export default function FormulaireList() {
    const [formulaires, setFormulaires] = useState([]);
    const [showaddFormulaire, setShowAddFormulaire] = useState(false);

    useEffect(() => {
        let isMounted = true;

        getFormulaire()
            .then(res => {
                if (isMounted) {
                    console.log("res from Api ", res);
                    setFormulaires(res);
                }
            })
            .catch(error => {
                console.error("Error fetching formulaires:", error);
            });

        return () => {
            isMounted = false;
        };
    }, []);

    const handleAddSubmit = (formData) => {
        addFormulaire(formData)
            .then(res => {
                setFormulaires([...formulaires, res]); // Add the new formulaire to the existing array
                setShowAddFormulaire(false);
            })
            .catch(error => {
                console.error("Error adding formulaire:", error);
            });
    };

    return (
        <>
            <h3>Formulaire</h3>
            <table>
                <thead>
                    <tr>
                        <th>Nom</th>
                        <th>Pr√©nom</th>
                        <th>Email</th>
                    </tr>
                </thead>
                <tbody>
                    {formulaires.map(formulaire => (
                        <tr key={formulaire.id}>
                            <td>{formulaire.nom}</td>
                            <td>{formulaire.prenom}</td>
                            <td>{formulaire.mail}</td>
                            <td>
                                <form onSubmit={(e) => handleAddSubmit(e.target)}>
                                    <button type="submit">Envoyer</button>
                                </form>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={() => setShowAddFormulaire(true)}>Add New Person</button>
            {showaddFormulaire && <AddFormulaire handleAddSubmit={handleAddSubmit} />}
        </>
    );
}
