// FormulaireList.js
import React, { useEffect, useState } from "react";
import { getFormulaire, addFormulaire,editFormulaire } from "../services/ApiService";
import AddFormulaire from "./AddFormulaire";
import { EditFormulaire } from "./EditFormulaire";


export default function FormulaireList() {
    const [formulaires, setFormulaires] = useState([]);
    const [showAddFormulaireForm, setShowAddFormulaireForm] = useState(false);
    const [showEditFormulaireForm, setShowEditFormulaireForm] = useState(false);
    const[selectedEditData,setSelectedEditData]=useState()

    useEffect(() => {
        let isMounted = true;

        getFormulaire()
            .then(res => {
                if (isMounted) {
                    console.log("res from Api ", res);
                    setFormulaires(res);
                }
            })
            .catch(error => {
                console.error("Error fetching formulaires:", error);
            });

        return () => {
            isMounted = false;
        };
    }, []);

    const handleAddSubmit = (e) => {
        e.preventDefault();
        addFormulaire(e.target)
            .then(res => {
                setFormulaires([...formulaires, res]); // Append the new formulaire to the list
                setShowAddFormulaireForm(false); // Hide the form after successful submission
            })
            .catch(error => {
                console.error("Error adding formulaire:", error);
            });
    };
    const handleEditBtn =(formulaire) => {
        setSelectedEditData = formulaire
        setShowEditFormulaireForm = true
        setShowAddFormulaireForm = false
    }
    const handleEditSubmit =(e,id) => {
        editFormulaire(id, e.target)
        .the(res => {
            setFormulaires(res)
        })

    }

    return (
        <>
            <h3>Formulaire</h3>
            <table border={"2px"} cellPadding={"10px"}>
                <thead>
                    <tr>
                        <th>Nom</th>
                        <th>Pr√©nom</th>
                        <th>Email</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {formulaires.map(formulaire => (
                         <tr key={formulaire.id}>
                            <td>{formulaire.nom}</td>
                            <td>{formulaire.prenom}</td>
                            <td>{formulaire.mail}</td>
                            <td><button onClick={()=>handleEditBtn(formulaire)}>Edit</button> <button>Delete</button></td> 
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={() => setShowAddFormulaireForm(true)}>Add New Person</button>
            {showAddFormulaireForm && <AddFormulaire handleAddSubmit={handleAddSubmit} />}
            {showEditFormulaireForm && <EditFormulaire handleEditSubmit={selectedEditData} />}
        </>
    );
}
